pragma solidity ^ 0.5.0;

import "@openzeppelin/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts/lifecycle/Pausable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

/// @title Devoleum
/// @author Lorenzo Zaccagnini Elisa Romondia
/// @notice You can use this contract for tracking your supply chain
/// @dev All function calls are currently implemented without side effects
contract Devoleum is Ownable, Pausable {
    using SafeMath
    for uint256;

    //STRUCT MAPPINGS
    mapping(uint256 => History) public historyIdToHistoryInfo;

    mapping(uint256 => Step) public stepIdToStepInfo;

    mapping(address => Merchant) public addressToMerchant;

    //PERMISSIONED TO SIGNUP
    bool public signupPermissioned = true;

    //PERMISSION
    mapping(address => bool) public merchantPermission; //only for permissioned signup

    mapping(uint256 => mapping(address => bool)) public historyIdToPermissionedMerchants;

    //COUNTERS
    uint256 public historiesCounter = 0;
    uint256 private stepsCounter = 0;
    mapping(address => uint256) public merchantAddressToHistoriesCounter;

    //LISTS
    address[] public merchantsList;

    //MERCHANT SECTION
    function getMerchantsList() external view returns(address[] memory) {
        return merchantsList;
    }

    struct Merchant {
        uint256 createdAt;
        string URI;
        address owner;
    }

    modifier isMerchantNotExistent() {
        require(
            addressToMerchant[msg.sender].createdAt == 0,
            "Duplicates not allowed"
        );
        _;
    }

    modifier isSignupPermissioned() {
        if (signupPermissioned == true) {
            require(
                merchantPermission[msg.sender] == true,
                "you are not allowed to create a new account"
            );
        }
        _;
    }

    modifier isMerchantEnabled() {
        require(merchantPermission[msg.sender] == true, "merchant not allowed");
        require(addressToMerchant[msg.sender].createdAt != 0, "merchant not existent");
        _;
    }

    modifier isAllowedToAddStep(uint256 _id) {
        require(historyIdToHistoryInfo[_id].enabled == true, "history is not enabled");
        require(
            historyIdToPermissionedMerchants[_id][msg.sender] == true,
            "not allowed to edit the product history"
        );
        _;
    }

    event MerchantCreated(address _owner);

    /// @notice Creates a Merchant profile
    /// @param _URI The link to a JSON file
    function createMerchant(
        string calldata _URI
    ) external isSignupPermissioned isMerchantNotExistent whenNotPaused {
        Merchant memory newMerchant = Merchant(
            now,
            _URI,
            msg.sender
        );
        addressToMerchant[msg.sender] = newMerchant;
        merchantPermission[msg.sender] = true;
        merchantsList.push(msg.sender);
        emit MerchantCreated(msg.sender);
    }

    struct History {
        uint256 createdAt;
        address merchantAdr;
        bool enabled;
        string hashOfJson;
        string URI;
    }

    event HistoryCreated(address _owner, uint256 _id);

    /// @notice Creates an History
    /// @param _hashOfJson The hash of the linked JSON file
    /// @param _URI The link to a JSON file
    /// @return The numeric ID of the history
    function createHistory(
        string calldata _hashOfJson,
        string calldata _URI
    ) external isMerchantEnabled whenNotPaused
    returns(
        uint256 historyID
    ) {
        History memory newHistory = History(
            now,
            msg.sender,
            true,
            _hashOfJson,
            _URI
        );
        historiesCounter = historiesCounter.add(1);
        merchantAddressToHistoriesCounter[msg
            .sender] = merchantAddressToHistoriesCounter[msg.sender].add(1);
        historyIdToHistoryInfo[historiesCounter] = newHistory;
        historyIdToPermissionedMerchants[historiesCounter][msg.sender] = true;
        emit HistoryCreated(msg.sender, historiesCounter);
        return historiesCounter;
    }

    //STEP SECTION

    struct Step {
        uint256 createdAt;
        address merchantAdr;
        bool enabled;
        uint256 historyID;
        string hashOfJson;
        string URI;
    }

    event StepCreated(address _owner, uint256 _id);

    /// @notice Creates a Step
    /// @param _historyID The numeric ID of the History
    /// @param _hashOfJson The hash of the linked JSON file
    /// @param _URI The link to a JSON file
    /// @return The numeric ID of the Step
    function createStep(
        uint256 _historyID,
        string calldata _hashOfJson,
        string calldata _URI
    ) external isMerchantEnabled whenNotPaused isAllowedToAddStep(_historyID)
    returns(
        uint256 stepID
    ) {
        Step memory newStep = Step(
            now,
            msg.sender,
            true,
            _historyID,
            _hashOfJson,
            _URI
        );
        stepsCounter = stepsCounter.add(1);
        stepIdToStepInfo[stepsCounter] = newStep;
        emit StepCreated(msg.sender, stepsCounter);
        return stepsCounter;
    }

    event MerchantToggled(
        address _owner,
        uint256 _historyID,
        address _target,
        bool _isEnabled
    );

    /// @notice Allow or disable other merchants to add steps
    /// @param _historyID The numeric ID of the History
    /// @param _target Merchant address
    /// @param _toggle Allow or disable
    function toggleHistoryMerchant(
        uint256 _historyID,
        address _target,
        bool _toggle
    ) external isMerchantEnabled whenNotPaused {
        require(
            historyIdToHistoryInfo[_historyID].merchantAdr == msg.sender,
            "only the history owner can toggle other merchants"
        );
        historyIdToPermissionedMerchants[_historyID][_target] = _toggle;
        emit MerchantToggled(msg.sender, _historyID, _target, _toggle);
    }

    // GETTERS SECTION
    function getMerchantInfo(address _merchantAdr)
    external
    view
    returns(
        uint256 createdAt,
        string memory URI,
        address owner
    ) {
        return (
            addressToMerchant[_merchantAdr].createdAt,
            addressToMerchant[_merchantAdr].URI,
            addressToMerchant[_merchantAdr].owner
        );
    }

    /// @notice Get the History Info by a given ID
    /// @param _id The numeric ID of the History
    /// @return the History Info
    function getHistoryInfo(uint256 _id)
    external
    view
    returns(
        uint256 createdAt,
        address merchantAdr,
        bool enabled,
        string memory hashOfJson,
        string memory URI
    ) {
        return (
            historyIdToHistoryInfo[_id].createdAt,
            historyIdToHistoryInfo[_id].merchantAdr,
            historyIdToHistoryInfo[_id].enabled,
            historyIdToHistoryInfo[_id].hashOfJson,
            historyIdToHistoryInfo[_id].URI
        );
    }

    /// @notice Get the Step Info by a given ID
    /// @param _id The numeric ID of the Step
    /// @return the Step Info
    function getStepInfo(uint256 _id)
    external
    view
    returns(
        uint256 createdAt,
        address merchantAdr,
        bool enabled,
        uint256 historyID,
        string memory hashOfJson,
        string memory URI
    ) {
        return (
            stepIdToStepInfo[_id].createdAt,
            stepIdToStepInfo[_id].merchantAdr,
            stepIdToStepInfo[_id].enabled,
            stepIdToStepInfo[_id].historyID,
            stepIdToStepInfo[_id].hashOfJson,
            stepIdToStepInfo[_id].URI
        );
    }

    /// @notice Get the Histories ID Of a Merchant
    /// @param _owner Merchant address
    /// @return the Histories ID Of a Merchant
    function historiesOfMerchant(address _owner)
    external
    view
    returns(uint256[] memory ownerHistories) {
        if (historiesCounter == 0) {
            return new uint256[](0);
        } else {
            uint256[] memory result = new uint256[](historiesCounter);
            uint256 resultIndex = 0;
            uint256 itemId;

            for (itemId = 1; itemId <= historiesCounter; itemId++) {
                if (historyIdToHistoryInfo[itemId].merchantAdr == _owner) {
                    result[resultIndex] = itemId;
                    resultIndex++;
                }
            }

            return result;
        }
    }

    /// @notice Get the steps ID Of an History
    /// @param _historyID The numeric ID of the History
    /// @return the steps ID Of an History
    function stepsOfHistory(uint256 _historyID)
    external
    view
    returns(uint256[] memory ownerHistories) {
        if (stepsCounter == 0) {
            return new uint256[](0);
        } else {
            uint256[] memory result = new uint256[](stepsCounter);
            uint256 resultIndex = 0;
            uint256 itemId;

            for (itemId = 1; itemId <= stepsCounter; itemId++) {
                if (stepIdToStepInfo[itemId].historyID == _historyID) {
                    result[resultIndex] = itemId;
                    resultIndex++;
                }
            }

            return result;
        }
    }

    //OWNER SECTION

    event changePermissioned(bool _permissioned);

    /// @notice get if Signup is permissioned
    /// @return if Signup is permissioned
    function getPermissionedStatus()
    external
    view
    returns(bool _signupPermissioned) {
        return signupPermissioned;
    }

    /// @notice Toggle Signup permission
    /// @param _permissioned Toggle permission
    function toggleSignupPermissioned(bool _permissioned) external onlyOwner() {
        signupPermissioned = _permissioned;
        emit changePermissioned(_permissioned);
    }

    /// @notice Toggle Merchant permission
    /// @param _permission Toggle permission
    /// @param _target The target address
    function toggleMerchantPermission(bool _permission, address _target)
    external
    onlyOwner() {
        merchantPermission[_target] = _permission;
    }

    /// @notice This function disable or enable an asset
    /// @param _permission Toggle permission
    /// @param _isStep Is this a Step? If false it's an History
    /// @param _id  The numeric ID of the History or Step
    function toggleAsset(
        bool _permission,
        bool _isStep,
        uint256 _id
    ) external onlyOwner() {
        if (_isStep) {
            stepIdToStepInfo[_id].enabled = _permission;
        } else {
            historyIdToHistoryInfo[_id].enabled = _permission;
        }
    }

    /// @notice This function edits the Merchant JSON link
    /// @param _URI The link to a JSON file
    function editMerchURI(string calldata _URI) external isMerchantEnabled whenNotPaused {
        addressToMerchant[msg.sender].URI = _URI;
    }

    /// @notice This function edits the History JSON link
    /// @param _URI The link to a JSON file
    /// @param _id The numeric ID of the History
    function editHistoryURI(string calldata _URI, uint256 _id) external isMerchantEnabled whenNotPaused {
        require(historyIdToHistoryInfo[_id].merchantAdr == msg.sender || isOwner(), "only the history owner can edit the URI");
        historyIdToHistoryInfo[_id].URI = _URI;
    }
    /// @notice This function edits the Step JSON link
    /// @param _URI The link to a JSON file
    /// @param _id The numeric ID of the Step
    function editStepURI(string calldata _URI, uint256 _id) external isMerchantEnabled whenNotPaused {
        require(stepIdToStepInfo[_id].merchantAdr == msg.sender || isOwner(), "only the step owner can edit the URI");
        stepIdToStepInfo[_id].URI = _URI;
    }
}

